mapper.select:
SELECT O.ORDER_ID as orderId, O.QUANTITY as quantity, NVL((SELECT S.NAME FROM TRANSPORT_REQUEST TR JOIN STORE S ON TR.FROM_STORE_ID = S.STORE_ID WHERE TR.ORDER_ID = O.ORDER_ID), '발송X') as fromStore, CASE WHEN O.STATUS = 2 THEN '대기' WHEN O.STATUS = 0 THEN '배송중' WHEN O.STATUS = 1 THEN '픽업가능' WHEN O.STATUS = 3 THEN '픽업완료' END as pickupStatus FROM ORDERS O JOIN STORE S ON O.STORE_ID = S.STORE_ID JOIN PRODUCT P ON O.PRODUCT_ID = P.PRODUCT_ID WHERE O.STORE_ID = #{storeId}
AND (O.ORDER_ID LIKE '%' || #{keyword} || '%')
ORDER BY O.STATUS DESC, O.ORDER_ID ASC OFFSET #{offset} ROWS FETCH NEXT #{pageSize} ROWS ONLY

mapper.update:
UPDATE ORDERS
SET STATUS = #{status}
WHERE ORDER_ID = #{orderId}
