mapper.select:
SELECT store_id, quantity
FROM stock
WHERE product_id LIKE #{productId}

mapper.update:
UPDATE STOCK
SET QUANTITY = QUANTITY + #{quantityChange}
WHERE STORE_ID = #{storeId} AND PRODUCT_ID = #{productId}

mapper.select:
SELECT COUNT(*)
FROM STOCK
WHERE STORE_ID = #{storeId}
  AND PRODUCT_ID = #{productId}
